sys_script.script:
(function executeRule(current, previous /*null when async*/) {


	// Instantiate the NeedItUtils class.  Call the isDatePast method and pass 
	// the u_when_needed value.
	var niutil = new NeedItUtils();
	gs.addInfoMessage(niutil+ "  == Instantiate NeedItUtils class which is actually the name of script include");



	//CHECK FOR PAST DATE
	var isPast = niutil.isDatepast(current.u_when_needed);
	//isPast+ ' == call of isDatepast method which is actually the function of NeedItUtils script include and passed current WHEN NEEDED value');
	// If the isDatePast method returns true, the date is in the past.
	if(isPast==true){

		gs.addErrorMessage("When needed date cannot be in the past. Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

	//CHECK FOR TODAY'S DATE (same as above)
	var niut = new presentNeedItUtils();
	var present = niut.isDateToday(current.u_when_needed);
	if(present==true){
		gs.addErrorMessage('You cannot submit NeedIt requests for today.');
		current.setAbortAction(true);
	}
})(current, previous);
