sys_script_client.script:
function onLoad() {
    if(g_form.isNewRecord()){ 
	g_form.setValue('u_requested_for', g_user.getUserID());
	}
	g_form.flash("u_requested_for_email","#00FF00",2); //label will flash for FOUR seconds
	//To find information for runtime problems, use JavaScript's Try/Catch statement.
	// Using Try/Catch to trap runtime errors.  
  // the function helloWorld() function does not exist. Although the script does not have syntax errors, it throws a runtime error.

	try{
		helloWorld();
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message);
	}


	//javasript field watcher jslog() used for dubugging client scripts
	   jslog('Ahh..whats up doc');


}
