sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatepast:function(strdate){
	//Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var whats = new GlideDateTime(strdate);
		// If the testdate is before rightnow, return true, else return false
		if(whats.before(rightnow)){
			gs.addInfoMessage('Business rule "NeedIt When needed field date 1" called Script Include "NeedItUtils", where the SI function"isDatepast" returned true. This function passed current WHEN NEEDED value');

gs.addErrorMessage('ACTION ABORTED as Business rule "NeedIt When needed field date 1" called Script Include "NeedItUtils"');
		return true;


		}
		else{

	gs.addInfoMessage('Business rule "NeedIt When needed field date 1" also called Script Include "NeedItUtils", where the SI function"isDatepast" returned false. This function passed current WHEN NEEDED value');

			return false;

		}




	},

    type: 'NeedItUtils'
};
