sysauto_script.script:
//get today's time and date
var n = new GlideDateTime();

var quer = new GlideRecord('x_58872_needit_needit');
quer.addQuery('u_when_needed','<=',n);
quer.addQuery('state','!=',3); // a State field value of Closed Complete.
quer.query();


while(quer.next()){
	gs.eventQueue('x_58872_needit.overdueNeedItTask', quer ,quer.number,gs.getUserName(),'needit_queue' ); //Use the fifth parameter in gs.eventQueue() to send the event to a custom queue 'needit_queue' that was made from copying "text index event process" schedule job. Enclose the queue name in either single or double quote marks
	gs.info('OverDue tasks records are '+ quer.number);

}
